function filter_products()
{
  
  var products = $('.category-product-list [p_id]').map(function () {
    return {
      'id'        : $(this).attr('p_id'),
      'color'     : $(this).attr('p_color'),
      'size'      : $(this).attr('p_size'),
      'from'      : $(this).attr('p_from'),
      'price'     : $(this).attr('p_price_per_unit'),
      'visibility': 'hidden'
    };
  });
  
  $('.search-and-filter input[type=checkbox]:checked, .search-and-filter .filter-button').each(function (i, v) {
    var subject = $(this);
    switch (subject.attr('target'))
    {
      case 'color':
        products = _.map(products, function (v) {
          var item = v;
          (v.color == subject.attr('value')) ? item.visibility = 'visible' : false;
          return item;
        });
        break;
      case 'size':
        products = _.map(products, function (v) {
          var item = v;
          (v.size == subject.attr('value')) ? item.visibility = 'visible' : false;
          return item;
        });
        break;
      case 'from':
        products = _.map(products, function (v) {
          var item = v;
          (v.from == subject.attr('value')) ? item.visibility = 'visible' : false;
          return item;
        });
        break;
      case 'price':
        products = _.map(products, function (v) {
          var item = v;
          var min_price = subject.parent().parent().find('[name=min]').val()*1;
          var max_price = subject.parent().parent().find('[name=max]').val()*1;
          (v.price*1 >= min_price && v.price*1 <= max_price) ? item.visibility = 'visible' : false;
          return item;
        });
        break;
    }
  });
  
  $('.product-thumb-1 :has([p_id])').parent().addClass('hidden');
  
  var visible_items = _.map(products, function(v){
    if(v.visibility == 'visible'){
      $('.product-thumb-1 :has([p_id='+v.id+'])').parent().removeClass('hidden');
    }
  });

}

