function lazyLoadImages(){
	
	$('[lazy-src]').each(function(i){
		
		var img = $(this);
		
		setTimeout(function(){
			
			img.attr('src', img.attr('lazy-src') ).removeAttr('lazy-src');
			
		}, 500 + 150 * i );
		
	});
	
}

